import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";

const isDuplicate = (name, existingContacts) =>
  existingContacts.some((contact) => contact.name === name);

export const ContactsPage = (props) => {
  /*
  Define state variables for 
  contact info and duplicate check
  */
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");

  useEffect(() => {
    const duplicateCheck = isDuplicate(name, props.contacts);
    if (duplicateCheck) {
      console.log("Contact already exists: " + name);
    }
  }, [name]);

  /*
  Add contact info and clear data
  if the contact name is not a duplicate
  */
  const handleSubmit = (e) => {
    e.preventDefault();
    try {
      setName(e.target.elements.name.value);
      setPhone(e.target.elements.phone.value);
      setEmail(e.target.elements.email.value);

      const duplicateCheck = isDuplicate(name, props.contacts);
      if (duplicateCheck) {
        console.log("Contact already exists: " + name);
      } else {
        props.handleContacts(name, phone, email);

        // reset the form fields;
        e.target.reset();
      }
    } catch (e) {
      console.error("Error creating contact: " + e.message);
    }
  };

  /*
  Using hooks, check for contact name in the 
  contacts array variable in props
  */

  return (
    <div>
      <section>
        <h2>Add Contact</h2>
        <ContactForm name={name} phone={phone} email={email}handleSubmit={handleSubmit} />
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
        <TileList contactsFromApp={props.contacts} />
      </section>
    </div>
  );
};
