import React, { useState } from "react";
import {
  RouterProvider,
  createBrowserRouter,
  createRoutesFromElements,
  Route,
  Navigate,
} from "react-router-dom";
import Root, { ROUTES } from "./components/root/Root";
import { AppointmentsPage } from "./containers/appointmentsPage/AppointmentsPage";
import { ContactsPage } from "./containers/contactsPage/ContactsPage";

function App() {
  const [contacts, setContacts] = useState("a");
  const [appointments, setAppointments] = useState([
    { name: "c", contact: "3", date: "", time: "" },
    { name: "d", contact: "4", date: "", time: "" },
  ]);

  /*
  Implement functions to add data to
  contacts and appointments
  */

  function handleContacts(obj) {
    try {
      const newContact = {
        name: obj.name,
        phone: obj.phone,
        email: obj.email,
      };
      setContacts([...contacts, newContact]);
    } catch (e) {
      console.error("Error adding new Contact: " + e.message);
    }
  }

  function handleAppointments(arr) {
    try {
      const newAppointment = arr.map((a) => ({
        name: a.name,
        contact: a.contact,
        date: a.date,
        time: a.time,
      }));
      setAppointments([...appointments, newAppointment]);
    } catch (e) {
      console.error("Error adding new Appointment: " + e.message);
    }
  }

  const router = createBrowserRouter(
    createRoutesFromElements(
      <Route path="/" element={<Root />}>
        <Route index element={<Navigate to={ROUTES.CONTACTS} replace />} />
        <Route
          path={ROUTES.CONTACTS}
          element={
            <ContactsPage
              contacts={contacts}
              handleContacts={handleContacts}
            /> /* Add props to ContactsPage */
          }
        />
        <Route
          path={ROUTES.APPOINTMENTS}
          element={
            <AppointmentsPage
              appointments={appointments}
              handleAppointments={handleAppointments}
            /> /* Add props to AppointmentsPage */
          }
        />
      </Route>
    )
  );

  return <RouterProvider router={router} />;
}

export default App;
