import React, { useState } from "react";
import {
  RouterProvider,
  createBrowserRouter,
  createRoutesFromElements,
  Route,
  Navigate,
} from "react-router-dom";
import Root, { ROUTES } from "./components/root/Root";
import { AppointmentsPage } from "./containers/appointmentsPage/AppointmentsPage";
import { ContactsPage } from "./containers/contactsPage/ContactsPage";

function App() {
  const [contacts, setContacts] = [
    {
      name: "John",
      "ph#": 1237894560,
      email: "john@example.com",
    },
  ];
  const [appointments, setAppointments] = [
    {
      appointmentName: "App 1",
      contact: "John",
      date: "02/01/2015",
      time: "10:00",
    },
  ];

  /*
  Implement functions to add data to
  contacts and appointments
  */

  function addContact(name, ph, email) {
    setContacts([{ ...contacts }, { name, ph, email }]);
  }

  function addAppointment(name, contact, date, time) {
    setAppointments([{ ...appointments }, { name, contact, date, time }]);
  }

  const router = createBrowserRouter(
    createRoutesFromElements(
      <Route path="/" element={<Root />}>
        <Route index element={<Navigate to={ROUTES.CONTACTS} replace />} />
        <Route
          path={ROUTES.CONTACTS}
          element={<ContactsPage contact={contacts} /> /* Add props to ContactsPage */}
        />
        <Route
          path={ROUTES.APPOINTMENTS}
          element={<AppointmentsPage /> /* Add props to AppointmentsPage */}
        />
      </Route>
    )
  );

  return <RouterProvider router={router} />;
}

export default App;
