import React, { useState, useEffect } from "react";

import { ContactForm } from "../../components/contactForm/ContactForm";
import { TileList } from "../../components/tileList/TileList";

const isDuplicate = (newContacts, existingContacts) =>
  existingContacts.some((existingC) =>
    newContacts.some((newC) => newC.name === existingC.name)
  );

export const ContactsPage = (props) => {
  /*
  Define state variables for 
  contact info and duplicate check
  */
  const [contacts, setContacts] = useState(props.contacts || []);

  useEffect(() => {
    const duplicateCheck = isDuplicate(contacts, props.contacts);
    //   contacts.some((contact) =>
    //   props.contacts.some((propContact) => propContact.name === contact.name)
    // );
    if (duplicateCheck) {
      console.log("Contact already exists: " + contacts.name);
    }
  }, [props.contacts]);

  /*
  Add contact info and clear data
  if the contact name is not a duplicate
  */
  const handleSubmit = (e) => {
    e.preventDefault();

    try {
      const newContactData = {
        name: e.target.elements.name.value,
        phone: e.target.elements.phone.value,
        email: e.target.elements.email.value,
      };

      const duplicateCheck = isDuplicate(newContactData, contacts);
      if (duplicateCheck) {
        console.log("Contact already exists: " + newContactData.name);
      } else {
        setContacts([...contacts, newContactData]);
        // reset the form fields;
        e.target.reset();
      }
    } catch (e) {
      console.error("Error creating contact: " + e.message);
    }
  };

  /*
  Using hooks, check for contact name in the 
  contacts array variable in props
  */

  return (
    <div>
      <section>
        <h2>Add Contact</h2>
        <ContactForm contacts={contacts} handleSubmit={handleSubmit} />
      </section>
      <hr />
      <section>
        <h2>Contacts</h2>
        <TileList contactsFromApp={props.contacts} />
      </section>
    </div>
  );
};
